sql.where:
 1 = 1 and deleted = 0

				and id = #{params.id} 


				and createTime = #{params.createTime} 


				and updateTime = #{params.updateTime} 


				and title = #{params.title} 


				and footPointType = #{params.footPointType} 


				and img = #{params.img} 


				and deleted = #{params.deleted}

mapper.select:
select * from footPoint t where 1=1 and t.deleted = 0
and t.footPointType = #{footPointType}
order by t.updateTime desc limit 12
